import React, { Component } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
  } from "react-router-dom";
import "../App.css";
const axios = require('axios');


class SignUp extends Component { 


    state = { 
        password : null,
        email : null,
        name : null,
    }

    signUp(e) {
        e.preventDefault();
        console.log(e);
        axios.get("http://localhost:3005/signup", {
            params: {
            email: this.state.email,
            password : this.state.password,
            name : this.state.name,
            }
        }).then((res) => {console.log(res); return(res);}).then((res) => this.setState({password: res.data}))
        console.log("signUp pressed.");
    }

    /*signUp() {    
        this.setState({      
            password: "Something",      
        });  
      }*/

    handlePasswordChange(event){
        this.setState({password : event.target.value}) 
    }

    render () {       

        return (
          <div>
               <div id='signupContainer'>
                    <form id='form'>       
                        <input className='input' type="text"   
                         placeholder="First Name"/>
                        <input className='input' type="text"  
                         placeholder="Last Name"/>          
                        <input className='input' type="text"  
                         placeholder="Email"/>          
                        <input className='input' type="password" 
                         placeholder="Password" value={this.state.password} onChange={this.handlePasswordChange}/>
                        <button onClick={(e) => this.signUp(e)}>
                            Sign Up
                        </button>
                    </form>
               </div>
          </div>
        )
     }
  }

  class SignIn extends Component { 
    state = { 
    }

    render () {                                   
        return (
          <div>
               <div id='signinContainer'>
                    <form id='form'>                
                        <input className='input' type="text"  
                         placeholder="Email"/>          
                        <input className='input' type="password" 
                         placeholder="Password"/>
                        <button className='button' id='submit'>Sign In</button>
                    </form>
               </div>
          </div>
        )
     }
  }

function Base(){
    return (
        <ul className="App-link">
        <li>
            <Link to="signin" className="App-link"> Sign In</Link>
        </li>
        <li>
            <Link to="signup" className="App-link">Sign Up</Link>
        </li>
    </ul>
    );
}

export default class Home extends Component {
    state = { 
    }
    render () {                                   
       return (
        <Router>
        <div id='container'>
            <Base/>
            <Switch>
                <Route path="/signin">
                    <SignIn />
                </Route>
                <Route path="/signup">
                    <SignUp />
                </Route>
                <Route path="/">
                    <h2>Welcome!</h2>
                </Route>
            </Switch>
        </div>
    </Router>


       )
    }
 }

